mapper.select:
SELECT ANNO_ID , ANNO_TITLE , ANNO_CONTENT , ANNO_CREATEDATE FROM TB_ANNOUNCEMENT
AND ANNO_ID <= #{findId}
ORDER BY ANNO_CREATEDATE DESC FETCH NEXT #{limit} + 1 ROWS ONLY

mapper.insert:

    SELECT CASE WHEN MAX(ANNO_ID) IS NULL THEN 1 ELSE MAX(ANNO_ID)+1 END AS ANNO_ID
    FROM TB_ANNOUNCEMENT

INSERT INTO TB_ANNOUNCEMENT(
ANNO_ID
, ANNO_TITLE
, ANNO_CONTENT
, ANNO_CREATEDATE
) VALUES (
#{annoId}
, #{annoTitle}
, #{annoContent}
, TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
)

mapper.update:
UPDATE TB_ANNOUNCEMENT
SET ANNO_TITLE   = #{annoTitle}
  , ANNO_CONTENT = #{annoContent}
WHERE ANNO_ID = #{annoId}
