mapper.select:
SELECT COUNT(*)
FROM TB_USER
WHERE USER_EMAIL = #{userEmail}

SELECT COUNT(*)
FROM TB_USER
WHERE USER_EMAIL = #{userEmail}

SELECT COUNT(*)
FROM TB_USER

    AND USER_REFRESH_TOKEN = #{userRefreshToken}

SELECT COUNT(*)
FROM TB_USER
WHERE USER_EMAIL = #{userEmail}
  AND USER_PASSWORD = #{userPassword}

mapper.insert:
INSERT INTO TB_USER ( USER_PASSWORD
                    , USER_NICKNAME
                    , USER_BIRTHDAY
                    , USER_GENDER
                    , USER_EMAIL
                    , USER_PHONE_NUMBER
                    , USER_PRIVACY_CONSENT
                    , USER_LOGIN_TYPE
                    , USER_REFRESH_TOKEN
                    , USER_ACCESS_TOKEN)
VALUES ( #{userPassword}
       , #{userNickname}
       , #{userBirthday}
       , #{userGender}
       , #{userEmail}
       , #{userPhoneNumber}
       , #{userPrivacyConsent}
       , #{userLoginType}
       , #{userRefreshToken}
       , #{userAccessToken})

mapper.update:
UPDATE tb_user
SET USER_NAME            = #{userName}
  , USER_PASSWORD        = #{userPassword}
  , USER_NICKNAME        = #{userNickname}
  , USER_BIRTHDAY        = #{userBirthday}
  , USER_GENDER          = #{userGender}
  , USER_EMAIL           = #{userEmail}
  , USER_PHONE_NUMBER    = #{userPhoneNumber}
  , USER_PRIVACY_CONSENT = #{userPrivacyConsent}
  , USER_UPDATE_DATE     = TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
WHERE USER_EMAIL = #{userEmail}

UPDATE tb_user
SET USER_REFRESH_TOKEN = #{userRefreshToken},
    USER_ACCESS_TOKEN  = #{userAccessToken}
WHERE USER_EMAIL = #{userEmail}

mapper.delete:
DELETE
FROM TB_USER
WHERE USER_EMAIL = #{userId}
  AND USER_PASSWORD = #{userPassword}

mapper.select:
SELECT user_login_type
FROM tb_user
WHERE user_access_token = #{authorization}
